<?php 
$EncryptedContent = '';
$keyData =  'Ilc4p5tcroti71GH0ivoVABD223Y7aXimRQoTJikuoo=';
$decoded = base64_decode($EncryptedContent);
$keyData = base64_decode($keyData);
if (empty($keyData)){
    exit("کلید لایسنس خالی است");
}
if ($decoded === false) {
    exit("PHPENCODER: Encoding Failure");
}
if (mb_strlen($decoded, '8bit') < (SODIUM_CRYPTO_SECRETBOX_NONCEBYTES + SODIUM_CRYPTO_SECRETBOX_MACBYTES)) {
    exit("PHPENCODER: اسکریپت قابل خوانش نیست");
}
$nonce = mb_substr($decoded, 0, SODIUM_CRYPTO_SECRETBOX_NONCEBYTES, '8bit');
$cipher = mb_substr($decoded, SODIUM_CRYPTO_SECRETBOX_NONCEBYTES, null, '8bit');
$plain = sodium_crypto_secretbox_open($cipher, $nonce, $keyData);
if ($plain === false) {
    exit("PHPENCODER: خطا در استخراج اسکریپت");
}
sodium_memzero($cipher);
sodium_memzero($keyData);
return eval('?>' . $plain);